INSERT INTO lessons (title, content, animation_type, animation_config, lesson_order) VALUES (
  'BÉ™rabÉ™rsizliklÉ™r vÉ™ Ä°ntervallar Ãœsulu',
  E'# BÉ™rabÉ™rsizliklÉ™r vÉ™ Ä°ntervallar Ãœsulu\n\n## Kurikulum Konteksti\n9-cu sinif, VII BÃ¶lmÉ™ - BÉ™rabÉ™rsizliklÉ™r\n\n## NÉ™ Ã¼Ã§Ã¼n Vacibdir?\n\nReal hÉ™yatda tÉ™nliklÉ™rdÉ™n daha Ã§ox iÅŸlÉ™dilir!\n\n**MÉ™sÉ™lÉ™lÉ™r:**\n- "BalansÄ±nÄ±z 50 manatdan Ã§ox olmalÄ±dÄ±r" â†’ x > 50\n- "Lift maksimum 500 kq daÅŸÄ±ya bilÉ™r" â†’ x â‰¤ 500\n- "Temperatur 18-25Â°C arasÄ±nda" â†’ 18 â‰¤ T â‰¤ 25\n\n## Æsas BÉ™rabÉ™rsizlik NÃ¶vlÉ™ri\n\n### 1. XÉ™tti\n2x + 5 < 13\n\n### 2. Kvadrat\nxÂ² - 5x + 6 > 0\n\n### 3. Rasional\n(x - 2)/(x + 3) â‰¥ 0\n\n## HÉ™ll ÃœsullarÄ±\n\n### Ãœsul 1: CÉ™bri (xÉ™tti Ã¼Ã§Ã¼n)\n2x + 5 < 13\n2x < 8\nx < 4\n\n### Ãœsul 2: Qrafik\nFunksiyalarÄ±n qrafiklÉ™rini Ã§É™kib kÉ™siÅŸmÉ™ tapÄ±rÄ±q.\n\n### Ãœsul 3: Ä°ntervallar Ãœsulu\n\n1. BÉ™rabÉ™rsizliyi sÄ±fÄ±ra bÉ™rabÉ™r edirik\n2. KÃ¶klÉ™ri tapÄ±rÄ±q\n3. ÆdÉ™d oxunda intervallarÄ± qeyd edirik\n4. HÉ™r intervalda iÅŸarÉ™ni yoxlayÄ±rÄ±q\n\n## Kvadrat BÉ™rabÉ™rsizlik NÃ¼munÉ™si\n\n**MÉ™sÉ™lÉ™:** xÂ² - 5x + 6 > 0\n\n### HÉ™ll:\n\n1. xÂ² - 5x + 6 = 0\n2. (x - 2)(x - 3) = 0\n3. xâ‚ = 2, xâ‚‚ = 3\n4. Ä°ntervallar:\n   - x < 2: (+) âœ“\n   - 2 < x < 3: (-) âœ—\n   - x > 3: (+) âœ“\n5. **Cavab:** x âˆˆ (-âˆ, 2) âˆª (3, +âˆ)\n\n## Rasional BÉ™rabÉ™rsizlik\n\n**MÉ™sÉ™lÉ™:** (x - 2)/(x + 3) â‰¥ 0\n\n### HÉ™ll:\n\n1. Pay: x - 2 = 0 â†’ x = 2\n2. MÉ™xrÉ™c: x + 3 = 0 â†’ x = -3 (mÉ™hdudiyyÉ™t!)\n3. Ä°ÅŸarÉ™ sxemi:\n   - x < -3: (-)\n   - -3 < x < 2: (-)\n   - x > 2: (+)\n4. â‰¥ 0 Ã¼Ã§Ã¼n: (+) vÉ™ ya sÄ±fÄ±r\n5. **Cavab:** x âˆˆ (-âˆ, -3) âˆª [2, +âˆ)\n\nNOT: x = -3 daxil deyil (mÉ™xrÉ™c sÄ±fÄ±r olar)\n\n## NÃ¼munÉ™ MÉ™sÉ™lÉ™\n\n**MÉ™sÉ™lÉ™:** xÂ² - 7x + 10 â‰¤ 0\n\n### HÉ™ll:\n\n1. xÂ² - 7x + 10 = 0\n2. xâ‚ = 2, xâ‚‚ = 5\n3. (x - 2)(x - 5) â‰¤ 0\n4. Ä°ÅŸarÉ™ sxemi:\n   - x < 2: (+)\n   - 2 â‰¤ x â‰¤ 5: (-) âœ“\n   - x > 5: (+)\n5. **Cavab:** x âˆˆ [2, 5]\n\n## Ev TapÅŸÄ±rÄ±qlarÄ±\n\n1. Avtobusda 45 yer var. Bilet 2 manat. Æn azÄ± 60 manat qazanmaq lazÄ±mdÄ±r. Minimum neÃ§É™ bilet satmalÄ±?\n\n2. (xÂ² - 9)/(x - 2) > 0